// ==UserScript==
// @name          GithubGoTop(FIX)
// @name:CN-zh_cn Github一键返回顶部
// @version       0.5.1
// @description   scrolltop
// @author        gaojr, maboloshi
// @namespace     https://github.com/maboloshi/UserScripts
// @license       MIT
// @match         https://*github.com/*
// @grant         GM_addStyle
// @connect       github.com
// @icon          https://github.githubassets.com/pinned-octocat.svg
// @note          原 https://github.com/gaojr/scripts-styles/blob/master/scripts/github-go-top.user.js 修复版
// @note          1. 打印页面时隐藏图标
// @note          2. 修复因 GitHub 新`ajax`载入方式, 导致切换页面时图标消失
// @note          3. 自适应系统或 GitHub 明暗主题, 自动切换明暗模式
// ==/UserScript==

(function () {

  function AddIcon() {
      GM_addStyle("@media print { #GoTop { display: none !important; } }");
      var body = document.querySelector('body');
      var goTop = document.createElement('div');
      var imgBox = document.createElement('img');
      goTop.style.position = 'fixed';
      goTop.style.right = '0%';
      goTop.style.bottom = '0%';
      goTop.style.cursor = 'pointer';
      goTop.style.zIndex = '999';
      imgBox.id = "GoTop";
      imgBox.src =
      //  'data:image/svg+xml;base64,';
         'data:image/png;base64,';
      imgBox.style.height = '48px';
      imgBox.style.width = '48px';
      goTop.appendChild(imgBox);
      body.appendChild(goTop);
      goTop.onclick = function () {
        document.body.scrollTop = document.documentElement.scrollTop = 0;
      };
    }

  function ToggleMode() {
    var html = document.documentElement;
    var imgBox = document.getElementById('GoTop')
    var github_mode = html.getAttribute('data-color-mode');
    var system_dark = window.matchMedia && window.matchMedia('(prefers-color-scheme: dark)').matches;
    if(imgBox){
      imgBox.style.filter = "light" === github_mode ? "" : "dark" === github_mode || system_dark ? "invert(100%)" : "";
    }
  }

  AddIcon();
  ToggleMode();

  window.matchMedia && (window.matchMedia('(prefers-color-scheme: dark)').onchange = ToggleMode);//系统

  // 监视最顶层，仅当新增 BODY 时，添加图标
  new window.MutationObserver(function(mutations) {
      for(let mutation of mutations) {
          if (mutation.addedNodes.length > 0) { // 仅当节点增加
              for (const node of mutation.addedNodes){
                  if (node.nodeName === 'BODY') { // 增加的节点为 BODY
                      AddIcon();
                      ToggleMode();
                      return;
                  }
              }
          } else if(mutation.type === 'attributes') {
            ToggleMode();
          }
      }
  }).observe(document.documentElement, {
    childList: true,
    attributeFilter: ['data-color-mode']
  });

})();
